mapper.select:
SELECT f.foodName
FROM food f
JOIN twenty_attribute t ON f.foodId = t.foodId


         AND f.category = 'Korean food' 
         AND f.category = 'Western food' 
         AND f.category = 'Chinese food' 
         AND f.category = 'Japanese food' 
         AND f.category = 'Asian food' 

             AND f.category != 'Korean food' 
             AND f.category != 'Western food' 
             AND f.category != 'Chinese food' 
             AND f.category != 'Japanese food' 
             AND f.category != 'Asian food' 



     AND t.is_soup = #{isSoup} 
     AND t.is_spicy = #{isSpicy} 
     AND t.is_fried = #{isFried} 
     AND t.is_roasted = #{isRoasted} 
     AND t.has_pork = #{hasPork} 
     AND t.has_beef = #{hasBeef}

SELECT foodId, foodName, category, description, imagePath
FROM food
WHERE foodName = #{name}
