mapper.insert:
INSERT INTO user (
id, password, nickname, name, email, birthDate, gender,
phoneNumber, likeMenu, disLikeMenu, role, status,
socialLoginSite, socialId
) VALUES (
#{id}, #{password}, #{nickname}, #{name}, #{email}, #{birthDate}, #{gender},
#{phoneNumber}, #{likeMenu}, #{disLikeMenu}, #{role}, #{status},
#{socialLoginSite}, #{socialId}
)

mapper.update:
UPDATE user
SET status = #{status}
WHERE user_id = #{user_id}

mapper.select:
