LIMIT #{round}

mapper.update:
/* [비유] food 테이블에서 foodId가 일치하는 음식을 찾아 winCount를 1 올립니다. */
UPDATE food
SET winCount = winCount + 1
WHERE foodId = #{foodId}

mapper.select:
SELECT * FROM user
WHERE id = #{id}
AND email = #{email}
AND status = 'ACTIVE'

